// import { render } from '@testing-library/react';
import React, { Component } from 'react';
import styles from './App.module.scss';
import {Link, Route, withRouter} from 'react-router-dom';
import HomePage from './HomePage';
import WorkPage from './WorkPage';
import ResumePage from './ResumePage';
import ContactPage from './ContactPage';
import {Wrap, Header, Box, Boxleft,Boxright,Linkstyle,Para, Content,Footer} from  './style';




class App extends Component {
  render(){
    // withRouter 提供的属性，获取当前路由
    const { location } = this.props;
    // console.log(location.pathname);
  return (
   <Wrap>
     {/* header nav */}
      <Header>
        <Box>
          <Boxleft>Yang LIU</Boxleft>
          <Boxright>
            {/* <Linkstyle as={Link} to="/" className={location.pathname==="/" ? styles.selected : null}><Para>Home</Para></Linkstyle>
            <Linkstyle as={Link} to="/about" className={location.pathname==="/about" ? styles.selected : null}><Para>About</Para></Linkstyle>
            <Linkstyle as={Link} to="/resume" className={location.pathname==="/resume" ? styles.selected : null}><Para>Resume</Para></Linkstyle>
            <Linkstyle as={Link} to="/contact" className={location.pathname==="/contact" ? styles.selected : null}><Para>Contact</Para></Linkstyle> */}

            <Linkstyle></Linkstyle>
          </Boxright>
          </Box>
      </Header>
      {/* content */}
      <Content>
        <Route path="/" exact component={HomePage} />
        <Route path="/about"  exact component={WorkPage} />
        <Route path="/resume" exact  component={ResumePage} />
        <Route path="/contact"  exact component={ContactPage} />
        {/* 动态路由，传递动态的id */}
        {/* <Route path="/works/:id"  exact component={ResumePage} /> */}
      </Content>
      {/* footer */}
      <Footer>
       <p>© 2021 All rights reserved. Designed by YangLIU</p>
      </Footer>
    </Wrap>
  );
}
}

export default withRouter(App);
