flex-flow: column wrap;
background-color: rgb(229, 241, 241);`

export const Brief = styled.div`
text-align: center;`

export const Photo = styled.img`
width: 260px;
height: 260px;
border-radius: 30px;
margin-top: 60px;
`

export const  Title = styled.h2`
margin-top: 30px;
padding: 12px 0;
font-size: 30px;`

export const List = styled.ul`
    display: flex;
    justify-content: center;
    list-style: none;

`

