// import { render } from '@testing-library/react';
import React, { Component } from 'react';
import styles from './App.module.scss';
import {Link, Route, withRouter} from 'react-router-dom';
import HomePage from './HomePage';
import WorkPage from './WorkPage';
import ResumePage from './ResumePage';
import ContactPage from './ContactPage';
import {Wrap, Header, Box, Boxleft,Boxright,Linkstyle } from  './style';




class App extends Component {
  render(){
    // withRouter 提供的属性，获取当前路由
    const { location } = this.props;
    // console.log(location.pathname);
  return (
   <Wrap>
     {/* header nav */}
      <Header>
        <Box>
          <Link to="/"><Boxleft>Yang LIU</Boxleft></Link>
          <Boxright>
            <Linkstyle as to="/" className={location.pathname==="/" ? styles.selected : null}><p>Home</p></Linkstyle>
            <Link to="/about" className={location.pathname==="/about" ? styles.selected : null}><p>About</p></Link>
            <Link to="/resume" className={location.pathname==="/resume" ? styles.selected : null}><p>Resume</p></Link>
            <Link to="/contact" className={location.pathname==="/contact" ? styles.selected : null}><p>Contact</p></Link>
          </Boxright>
          </Box>
      </Header>
      {/* content */}
      <section className={styles.content}>
        <Route path="/" exact component={HomePage} />
        <Route path="/about"  exact component={WorkPage} />
        <Route path="/resume" exact  component={ResumePage} />
        <Route path="/contact"  exact component={ContactPage} />
        {/* 动态路由，传递动态的id */}
        {/* <Route path="/works/:id"  exact component={ResumePage} /> */}
      </section>
      {/* footer */}
      <footer className={styles.footer}>
      <p> © 2021 All rights reserved. Designed by YangLIU</p>
      </footer>
      </Wrap>
  );
}
}

export default withRouter(App);
