import styled from 'styled-components';

export const Wrap = styled.div`
width: 100%;
padding: 30px 80px;
height: auto;
background-color: blanchedalmond;
display: flex;
flex: 1;
flex-wrap: wrap;
text-align: start;
`

export const Left = styled.div`
padding: 10px;
margin-top: 10px;
flex: 1;
border-radius: 15px;
background-color: white;
margin-right: 30px;

`


export const Right = styled.div`

padding: 10px;
margin-top: 20px;
flex: 3;
background-color: white;
border-radius: 15px;

`


export const Img = styled.img`
margin-top: 0;
width: 100%;    
height: 300px;
`

export const Title = styled.h4`
  font-size: 18px; 

  `

